
var width = 200,
    height = 150,
    radius = Math.min(width, height) / 2;

var color1 = d3.scale.ordinal()
    .range(["#98abc5", "#ff8c00"]);

var arc = d3.svg.arc()
    .outerRadius(radius - 10)
    .innerRadius(0);

var labelArc = d3.svg.arc()
    .outerRadius(radius - 40)
    .innerRadius(radius - 40);

var pie = d3.layout.pie()
    .sort(null)
    .value(function(d,i) 
           { 
    
        console.log(d.count);
        return d.count; 
            
    });

